mapper.select:
select 

from t_class
where 1=1
   
    and teacherId =  #{teacherId}
  
  
    and className like #{className}
  
  
    and experName like #{experName}

select 
	count(*) 
from  t_class
where 1=1
   
    and teacherId =  #{teacherId}
  
  
    and className like #{className}
  
  
    and experName like #{experName}
